-- create database bookstore;
--
-- create table users
-- (
--     id       bigint generated by default as identity primary key,
--     email    varchar(255),
--     name     varchar(255),
--     password varchar(255),
--     phone    varchar(255),
--     role     varchar(255),
--     active   boolean not null,
--     token    varchar(255),
--     username varchar(255)
-- );
--
-- create table customer
-- (
--     id      bigint generated by default as identity primary key,
--     address varchar(255),
--     email   varchar(255),
--     name    varchar(255),
--     phone   varchar(255)
-- );
--
-- create table supplier
-- (
--     id      bigint generated by default as identity primary key,
--     address varchar(255),
--     email   varchar(255),
--     name    varchar(255),
--     phone   varchar(255),
--     type    varchar(255),
--     website varchar(255)
-- );
--
-- create table book
-- (
--     id          bigint generated by default as identity primary key,
--     cost        double precision,
--     genre       varchar(255),
--     isbn        varchar(255),
--     name        varchar(255),
--     price       double precision,
--     stock       integer,
--     supplier_id bigint constraint fk_book_supplier_id references supplier
-- );
--
-- create table stockin
-- (
--     id           bigint generated by default as identity primary key,
--     approved_at  timestamp(6),
--     approved_by  varchar(255),
--     created_at   timestamp(6),
--     created_by   varchar(255),
--     date         date,
--     stage        varchar(255)
--         constraint stockin_stage_check
--             check ((stage)::text = ANY
--                    ((ARRAY ['SAVE'::character varying,
--                        'UPDATE'::character varying,
--                        'SUBMIT'::character varying,
--                        'APPROVE'::character varying,
--                        'REJECT'::character varying])::text[])),
--     submitted_at timestamp(6),
--     submitted_by varchar(255),
--     type         varchar(255),
--     invoice      varchar(255)
-- );
--
-- create table stockin_details
-- (
--     id         bigint generated by default as identity primary key,
--     pre_stock  integer,
--     quantity   integer,
--     book_id    bigint constraint fk_stockin_details_book_id references book,
--     stockin_id bigint constraint fk_stockin_details_stockin_id references stockin
-- );
--
-- create table sales
-- (
--     id          bigint generated by default as identity primary key,
--     date        date,
--     invoice     varchar(255),
--     type        varchar(255),
--     sale_by     bigint,
--     customer_id bigint constraint fk_sales_customer_id references customer,
--     stage       varchar(255)
--         constraint sales_stage_check
--             check ((stage)::text = ANY
--                    ((ARRAY ['SAVE'::character varying,
--                        'UPDATE'::character varying,
--                        'SUBMIT'::character varying,
--                        'APPROVE'::character varying,
--                        'REJECT'::character varying])::text[]))
-- );
--
-- create table sales_details
-- (
--     id       bigint generated by default as identity primary key,
--     quantity integer,
--     stock    integer,
--     book_id  bigint constraint fk_sales_details_book_id references book,
--     sales_id bigint constraint fk_sales_details_sales_id references sales
-- );

